import React, { useEffect, useState } from "react";
import SliderSlick from "react-slick";
import "./slider.css"; // Import tệp CSS

const Slider = () => {
  const [imgSliders, setImgSliders] = useState([]);
  let urlApiProduct = 'http://localhost:5000/api/product/all-product';

  const getProduct = async () => {
    const rs = await fetch(urlApiProduct);
    const imgs = await rs.json();
    setImgSliders(imgs?.data);
  };

  useEffect(() => {
    getProduct();
  }, []);

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <div className="slick-next">Next</div>,
    prevArrow: <div className="slick-prev">Prev</div>
  };

  return (
    <div className="w-[100%]">
      <SliderSlick {...settings}>
        {imgSliders?.map((el, index) => {
          return (
            <div key={index}>
              <img
                className="object-fill w-full h-[400px]"
                src={el?.img[index]}
                alt=""
              />
            </div>
          );
        })}
      </SliderSlick>
    </div>
  );
};

export default Slider;
