import React, { useEffect, useState } from "react";
// import getApiProduct from "../../api/getApiProduct";

const Slider = () => {
  const [imgSliders, setImgSliders] = useState([])
  const getProduct = async () => {
    const rs = await fetch('http://localhost:5000/api/product/all-product')
      const imgs = await rs.json()
      setImgSliders()
    }
  useEffect(() => {
    getProduct()
  }, [])
  return (
    <div className="w-full">
      <img
        className="object-cover w-full h-[350px]"
        src="https://www.concept-phones.com/wp-content/uploads/2020/05/iPhone-Slide-Pro-concept-design-4-1024x575.jpg"
        alt=""
      />
    </div>
  );
};

export default Slider;
