import React, { useEffect, useState } from "react";
import SliderSlick from "react-slick";
import { FaArrowRight, FaArrowLeft } from "react-icons/";

const Slider = () => {
  const [imgSliders, setImgSliders] = useState([]);
  let urlApiProduct = 'http://localhost:5000/api/product/all-product';

  const getProduct = async () => {
    const rs = await fetch(urlApiProduct);
    const imgs = await rs.json();
    setImgSliders(imgs?.data);
  };

  useEffect(() => {
    getProduct();
  }, []);

  const NextArrow = (props) => {
    const { className, style, onClick } = props;
    return (
      <div className={className} style={{ ...style }} onClick={onClick}>
        <FaArrowRight />
      </div>
    );
  };

  const PrevArrow = (props) => {
    const { className, style, onClick } = props;
    return (
      <div className={className} style={{ ...style }} onClick={onClick}>
        <FaArrowLeft />
      </div>
    );
  };

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    nextArrow: <NextArrow />,
    prevArrow: <PrevArrow />
  };

  return (
    <div className="w-[100%]">
      <SliderSlick {...settings}>
        {imgSliders?.map((el, index) => {
          return (
            <div key={index}>
              <img
                className="object-fill w-full h-[400px]"
                src={el?.img[index]}
                alt=""
              />
            </div>
          );
        })}
      </SliderSlick>
    </div>
  );
};

export default Slider;
