import React, { useEffect, useState } from "react";
// import getApiProduct from "../../api/getApiProduct";

const Slider = () => {
  const [imgSliders, setImgSliders] = useState([])
  const getProduct = async () => {
    const rs = await fetch('http://localhost:5000/api/product/all-product')
      const imgs = await rs.json()
      setImgSliders(imgs?.data)
    }
  useEffect(() => {
    getProduct()
  }, [])
  console.log(imgSliders)
  return (
    <div className="w-full">
      <img
        className="object- w-full h-[350px]"
        src={imgSliders[0]?.img[0]}
        alt=""
      />
    </div>
  );
};

export default Slider;
